import Myheader from "@/components/header.vue";
import Myfooter from "@/components/footer.vue";
import { reactive, toRefs } from "vue";
export default {
  name: "app",
  components: {
    Myfooter,
    Myheader
  },
  setup() {
    //这里存放数据
    const data = reactive({});
    return {
      ...toRefs(data)
    };
  },
  //监听属性 类似于data概念
  computed: {},
  //监控data中的数据变化
  watch: {
    $route(o, n) {
      document.title = "hua'bia";
    }
  },
  //方法集合
  methods: {},
  //生命周期 - 创建完成（可以访问当前this实例）
  created() {},
  //生命周期 - 挂载完成（可以访问DOM元素）
  mounted() {},
  beforeCreate() {},
  //生命周期 - 创建之前
  beforeMount() {},
  //生命周期 - 挂载之前
  beforeUpdate() {},
  //生命周期 - 更新之前
  updated() {},
  //生命周期 - 更新之后
  beforeDestroy() {},
  //生命周期 - 销毁之前
  destroyed() {},
  //生命周期 - 销毁完成
  activated() {} //如果页面有keep-alive缓存功能，这个函数会触发
};
