import { createStore } from 'vuex'
import axios from "axios";

export default createStore({
  state: {
    heroes: [],
    regional: [],
    races: [],
    roles: [],
  },
  getters: {
  },
  mutations: {
    sortHeroesByFormerly(state) {
      state.heroes.sort((a, b) => a.relevantRegion.localeCompare(b.formerly));
    },
    sortHeroesByRelevantRegion(state) {
      state.heroes.sort((a, b) => a.relevantRegion.localeCompare(b.relevantRegion));
    },
    sortHeroesByRelevantRegion(state) {
      state.heroes.sort((a, b) => a.relevantRegion.localeCompare(b.relevantRegion));
    },
    GET_HEROES(state, heroes) {
      state.heroes = heroes;
    },
    GET_REGIONAL(state, regional) {
      state.regional = regional;
    },
    GET_RACES(state, races) {
      state.races = races;
    },
    GET_ROLES(state, roles) {
      state.roles = roles;
    },
  },
  actions: {
    fetchHeroes({ commit }) {
      axios.get('http://localhost:8089/api/heroes')
        .then((response) => {
          commit('GET_HEROES', response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    },
    fetchRegional({ commit }) {
      axios.get('http://localhost:8089/api/region')
        .then((response) => {
          commit('GET_REGIONAL', response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    },
    fetchRaces({ commit }) {
      axios
        .get("http://localhost:8089/api/races")
        .then((response) => {
          commit('GET_RACES', response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    },
    fetchRoles({ commit }) {
      axios
        .get("http://localhost:8089/api/roles")
        .then((response) => {
          commit('GET_ROLES', response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    },
  },
  modules: {
  },
})
