import { createStore } from 'vuex'

export default createStore({
  state: {
    heroes: [],
    regional: [],
    races: [],
    roles: [],
  },
  getters: {
  },
  mutations: {
    GET_HEROES(state, heroes) {
      state.heroes = heroes;
    },
    GET_REGIONAL(state, regional) {
      state.regional = regional;
    },
    GET_RACES(state, races) {
      state.races = races;
    },
    GET_ROLES(state, roles) {
      state.roles = roles;
    },
  },
  actions: {
    fetchHeroes({ commit }) {
      axios.get('http://localhost:8089/api/heroes')
        .then((response) => {
          commit('SET_HEROES', response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    },
  },
  modules: {
  }
})
