import { createStore } from 'vuex'

export default createStore({
  state: {
    heroes: [],
    regional: [],
    races: [],
    roles: [],
  },
  getters: {
  },
  mutations: {
    GET_HEROES(state, heroes) {
      state.heroes = heroes;
    },
    GET_REGIONAL(state, regional) {
      state.regional = regional;
    },
    GET_RACES(state, races) {
      state.races = races;
    },
    GET_HEROES(state, roles) {
      state.heroes = roles;
    },
  },
  actions: {
  },
  modules: {
  }
})
