DROP TABLE IF EXISTS `hero_information`;
CREATE TABLE `hero_information`  (
  `releaseDate` datetime(0) NULL DEFAULT NULL COMMENT '发布日期',
  `formerly` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '原名',
  `translation` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '译名',
  `title` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '称号',
  `races` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '种族',
  `relevantRegion` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '相关地区',
  `role` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '位置',
  `biography` varchar(10000) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '传记',
  `synopsis` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '简介',
  `quote` varchar(250) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '座右铭、口头禅',
  `relatedPeople` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '相关人物',
  `posters` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '海报文件名',
  `width` int(5) NULL DEFAULT NULL COMMENT '海报宽',
  `height` int(5) NULL DEFAULT NULL COMMENT '海报高',
  `x` int(5) NULL DEFAULT NULL COMMENT '海报中心坐标x',
  `y` int(5) NULL DEFAULT NULL COMMENT '海报中心坐标y',
  PRIMARY KEY (`formerly`) USING BTREE,
  INDEX `role`(`role`) USING BTREE,
  INDEX `associated-faction`(`relevantRegion`) USING BTREE,
  INDEX `races`(`races`) USING BTREE,
  CONSTRAINT `hero_information_ibfk_1` FOREIGN KEY (`role`) REFERENCES `role_information` (`title`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `hero_information_ibfk_2` FOREIGN KEY (`relevantRegion`) REFERENCES `region_information` (`formerly`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `hero_information_ibfk_3` FOREIGN KEY (`races`) REFERENCES `races_information` (`formerly`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

